    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
           String studentID = jTextField1.getText();
    String fullName = jTextField3.getText();
    String programName = jComboBox1.getSelectedItem().toString();

    if (studentID.isEmpty() || fullName.isEmpty() || programName.equals("Select")) {
        JOptionPane.showMessageDialog(this, "Please fill in all the fields.", "Error", JOptionPane.ERROR_MESSAGE);
        return;
    }

    File inputFile = new File("students.txt");
    File tempFile = new File("temp_students.txt");

    boolean isUpdated = false;

    try (BufferedReader reader = new BufferedReader(new FileReader(inputFile));
         BufferedWriter writer = new BufferedWriter(new FileWriter(tempFile))) {

        String currentLine;
        while ((currentLine = reader.readLine()) != null) {
            // Check if the current line contains the matching student ID
            if (currentLine.startsWith("Student ID: " + studentID)) {
                // Replace the student details
                writer.write("Student ID: " + studentID);
                writer.newLine();
                writer.write("Full Name: " + fullName);
                writer.newLine();
                writer.write("Program Name: " + programName);
                writer.newLine();
                writer.write("--------------------------");
                writer.newLine();
                isUpdated = true;

                // Skip the next three lines (Full Name, Program Name, Separator)
                reader.readLine(); // Skip Full Name line
                reader.readLine(); // Skip Program Name line
                reader.readLine(); // Skip separator line
            } else {
                // Write the current line as-is
                writer.write(currentLine);
                writer.newLine();
            }
        }

        if (!isUpdated) {
            // If no matching student ID was found, append new student data
            writer.write("Student ID: " + studentID);
            writer.newLine();
            writer.write("Full Name: " + fullName);
            writer.newLine();
            writer.write("Program Name: " + programName);
            writer.newLine();
            writer.write("--------------------------");
            writer.newLine();
        }

        JOptionPane.showMessageDialog(this, isUpdated ?
                "Student information updated successfully!" :
                "Student ID not found. New information added!",
            "Success", JOptionPane.INFORMATION_MESSAGE);

    } catch (IOException e) {
        JOptionPane.showMessageDialog(this, "Error updating student information: " + e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
    }

    // Replace the old file with the updated file
    if (!inputFile.delete() || !tempFile.renameTo(inputFile)) {
        JOptionPane.showMessageDialog(this, "Error finalizing the update.", "Error", JOptionPane.ERROR_MESSAGE);
    } 
    }//GEN-LAST:event_jButton1ActionPerformed

